const { Pool } = require('pg');
require('dotenv').config();

// Agora você pode acessar as variáveis de ambiente usando process.env
const dbConfig = {
  user: process.env.DB_USER,
  password: process.env.DB_PASSWORD,
  host: process.env.DB_HOST,
  port: process.env.DB_PORT,
  database: process.env.DB_DATABASE,
};
export const pool = new Pool(dbConfig);

// Execute consultas SQL, como no exemplo anterior


// Certifique-se de encerrar a pool de conexão quando terminar
pool.end((error) => {
  if (error) {
    console.error('Erro ao encerrar a pool de conexão:', error);
  } else {
    console.log('Pool de conexão encerrada');
  }
});
