import { Usuario } from '../Service/controllers/Usuario.js';
import { pool } from '../Service/controllers/server.js';
import React,{useState} from 'react';
import "./Form.css";
const Form =()=>{
  const [placaSelecionada,setPlacaSelecionada] = useState("");
  const [modeloSelecionado,setModeloSelecionado] = useState("");
  const [clienteCadastrado,setCliente] = useState([]);

const handlerInputModel =(event)=>{
  const valorinserido = event.target.value;
  setModeloSelecionado(valorinserido);
 event.preventDefault();
}
const handlerInputPlaca =(event)=>{
  const valorinserido = event.target.value;
  setPlacaSelecionada(valorinserido);
 event.preventDefault();
}
// acessando o banco e consulta
const handlerInsertValue =(event)=>{
  pool.query('SELECT * FROM Estacionamento', (error, results) => {
    if (error) {
      console.error('Erro ao executar a consulta:', error);
    } else {
      console.log('Resultados da consulta:');
      console.log(results.rows);
  
      // Faça algo com os resultados aqui
    }
  });
  
  pool.end((error) => {
    if (error) {
      console.error('Erro ao encerrar a pool de conexão:', error);
    } else {
      console.log('Pool de conexão encerrada');
    }
  });




 event.preventDefault();
}
const handlerCadastro =(event)=>{
  const Cliente = new Usuario(modeloSelecionado,placaSelecionada);
  setCliente(Cliente);
 event.preventDefault();
}

  return(
    <div className="container__main__form">
      <div className="containerFormFundo">

      </div>
      <form id="form__container" onSubmit={handlerInsertValue}>
        <label htmlFor="modelo">Modelo</label>
        <input id="modelo" type="text" placeholder="Digite o modelo" value={modeloSelecionado} onChange={handlerInputModel} maxLength={30}/>
        <div className="placaEmodelo">
        <label htmlFor="placa">Placa</label>
        <input id="placa" type="text" placeholder="Digite a placa" value={placaSelecionada} onChange={handlerInputPlaca} maxLength={7} required/>
        </div>
        <div className="containerBtn">
           <input id="pesquisa" type="submit" value="Pesquisar"/>  
           <input id="cadastro" type="button" value="Cadastrar" onClick={handlerCadastro}/>
        </div>
      </form>
    </div>
  )
}
export default Form
