import React, { useState } from 'react';
import './Form.css';

const Form = () => {
  const [placaSelecionada, setPlacaSelecionada] = useState('');
  const [modeloSelecionado, setModeloSelecionado] = useState('');

  const handlerInputModel = (event) => {
    const valorinserido = event.target.value;
    setModeloSelecionado(valorinserido);
    event.preventDefault();
  };

  const handlerInputPlaca = (event) => {
    const valorinserido = event.target.value;
    setPlacaSelecionada(valorinserido);
    event.preventDefault();
  };

  const handlerSearch = (event) => {
    fetch('/api/veiculos')
    .then((response) => {
      if (!response.ok) {
        throw new Error('Erro ao buscar veículos');
      }
      return response.json();
    })
    .then((data) => {
      // Aqui você pode fazer o que desejar com os dados da API
      console.log('Dados da API:', data);
    })
    .catch((error) => {
      console.error('Erro ao consultar a API:', error);
    });
    event.preventDefault();
  };
  const handlerInsertValue = (event) => {
    // Lógica para fazer a solicitação POST para a API aqui
    event.preventDefault();
  };
  
  const handlerCadastro = (event) => {
    event.preventDefault();
  
    // Dados do veículo a serem inseridos
    const novoVeiculo = {
      modelo: modeloSelecionado,
      placa: placaSelecionada,
      horario: new Date().toLocaleString(), // ou você pode usar um formato específico de data/hora
    };
  
    // Fazendo uma solicitação POST à API
    fetch('/api/veiculos', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(novoVeiculo),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao criar veículo');
        }
        return response.json();
      })
      .then((data) => {
        // Aqui você pode fazer o que desejar com os dados da API
        console.log('Veículo criado com sucesso:', data);
      })
      .catch((error) => {
        console.error('Erro ao inserir veículo na API:', error);
      });
  };
  

  return (
    <div className="container__main__form">
      <div className="containerFormFundo"></div>
      <form id="form__container" onSubmit={handlerInsertValue}>
        <label htmlFor="modelo">Modelo</label>
        <input
          id="modelo"
          type="text"
          placeholder="Digite o modelo"
          value={modeloSelecionado}
          onChange={handlerInputModel}
          maxLength={30}
        />
        <div className="placaEmodelo">
          <label htmlFor="placa">Placa</label>
          <input
            id="placa"
            type="text"
            placeholder="Digite a placa"
            value={placaSelecionada}
            onChange={handlerInputPlaca}
            maxLength={7}
            required
          />
        </div>
        <div className="containerBtn">
          <input id="pesquisa" type="submit" value="Pesquisar" onClick={handlerSearch} />
          <input id="cadastro" type="button" value="Cadastrar" onClick={handlerCadastro} />
        </div>
      </form>
    </div>
  );
};

export default Form;
