import { Usuario } from '../Service/controllers/Usuario.js';
import React,{useState} from 'react';
import "./Form.css";
const Form =()=>{
  const express = require('express');
const bodyParser = require('body-parser');

const app = express();
const port = process.env.PORT || 3001;

app.use(bodyParser.json());
  const [placaSelecionada,setPlacaSelecionada] = useState("");
  const [modeloSelecionado,setModeloSelecionado] = useState("");

  
const veiculos = [
  {
    id: 1,
    modelo: modeloSelecionado,
    placa: placaSelecionada,
    horario: '00-00-00',
  },
];

const handlerInputModel =(event)=>{
  const valorinserido = event.target.value;
  setModeloSelecionado(valorinserido);
 event.preventDefault();
}
const handlerInputPlaca =(event)=>{
  const valorinserido = event.target.value;
  setPlacaSelecionada(valorinserido);
 event.preventDefault();
}
// acessando o banco e consulta
const handlerInsertValue =(event)=>{
  app.get('/api/veiculos', (req, res) => {
    res.json(veiculos);
  });
  app.get('/api/veiculos/:id', (req, res) => {
    const id = parseInt(req.params.id);
    const veiculo = veiculos.find((v) => v.id === id);
    if (!veiculo) {
      res.status(404).json({ error: 'Veículo não encontrado' });
    } else {
      res.json(veiculo);
    }
  });
  // Rota para criar um novo veículo
app.post('/api/veiculos', (req, res) => {
  const { modelo, placa, horario } = req.body;

  // Gerar um novo ID para o veículo
  const id = veiculos.length + 1;

  // Criar o novo veículo
  const novoVeiculo = { id, modelo, placa, horario };

  // Adicionar o veículo à lista
  veiculos.push(novoVeiculo);

  res.status(201).json(novoVeiculo);
});

app.listen(port, () => {
  console.log(`Servidor API está ouvindo na porta ${port}`);
});

  
 
